BEGIN {
    print "==============================================="
    print "         DEPARTMENT SALARY REPORT"
    print "==============================================="
    printf "%-15s %10s %10s %10s\n", "Department", "Employees", "Total", "Average"
    print "-----------------------------------------------"
}

{
    dept[$4]++
    total[$4] += $5
    if ($5 > max[$4]) max[$4] = $5
    if (min[$4] == 0 || $5 < min[$4]) min[$4] = $5
}

END {
    for (d in dept) {
        avg = total[d] / dept[d]
        printf "%-15s %10d  $%9d  $%9.0f\n", d, dept[d], total[d], avg
    }
    print "==============================================="
    print ""
    print "Salary Ranges by Department:"
    for (d in dept) {
        printf "  %s: $%d - $%d\n", d, min[d], max[d]
    }
}
EOF
