#!/usr/bin/env python3
"""Comprehensive file management tool."""
from pathlib import Path
import shutil
import argparse
from datetime import datetime

class FileManager:
    def __init__(self, directory="."):
        self.directory = Path(directory)

    def bulk_rename(self, pattern, prefix, dry_run=True):
        """Rename files matching pattern."""
        files = list(self.directory.glob(pattern))
        print(f"Found {len(files)} files\n")

        for file in sorted(files):
            parts = file.stem.split("_")
            number = parts[-1] if len(parts) >= 2 else file.stem
            new_name = f"{prefix}{number}{file.suffix}"

            if dry_run:
                print(f"[DRY RUN] {file.name} -> {new_name}")
            else:
                file.rename(file.parent / new_name)
                print(f"Renamed: {file.name} -> {new_name}")

    def organize_by_type(self, dry_run=True):
        """Organize files into folders by type."""
        file_types = {
            'Images': ['.jpg', '.png', '.gif'],
            'Videos': ['.mp4', '.avi', '.mkv'],
            'Documents': ['.pdf', '.doc', '.txt'],
            'Code': ['.py', '.js', '.sh'],
        }

        files = [f for f in self.directory.iterdir() if f.is_file()]

        for file in files:
            category = 'Other'
            for cat, exts in file_types.items():
                if file.suffix.lower() in exts:
                    category = cat
                    break

            target_dir = self.directory / category

            if dry_run:
                print(f"[DRY RUN] {file.name} -> {category}/")
            else:
                target_dir.mkdir(exist_ok=True)
                shutil.move(str(file), str(target_dir / file.name))
                print(f"Moved: {file.name} -> {category}/")

    def find_duplicates(self):
        """Find duplicate files by size and name."""
        from collections import defaultdict

        files_by_size = defaultdict(list)

        for file in self.directory.rglob('*'):
            if file.is_file():
                size = file.stat().st_size
                files_by_size[size].append(file)

        duplicates = {size: files for size, files in files_by_size.items() if len(files) > 1}

        if duplicates:
            print(f"Found {len(duplicates)} sets of potential duplicates:\n")
            for size, files in duplicates.items():
                print(f"Size: {size} bytes")
                for file in files:
                    print(f"  - {file}")
                print()
        else:
            print("No duplicates found.")

if __name__ == "__main__":
    parser = argparse.ArgumentParser(description="File Management Tool")
    parser.add_argument('action', choices=['rename', 'organize', 'duplicates'])
    parser.add_argument('--pattern', help="File pattern for rename")
    parser.add_argument('--prefix', help="New prefix for rename")
    parser.add_argument('--execute', action='store_true', help="Execute (not dry run)")

    args = parser.parse_args()
    fm = FileManager()

    if args.action == 'rename':
