#!/usr/bin/env python3
import re
import sys

def extract_errors(log_file):
    """Extract all ERROR and CRITICAL level logs."""
    error_pattern = re.compile(r'(ERROR|CRITICAL|FATAL)', re.IGNORECASE)

    errors = []

    print(f"Scanning {log_file} for errors...\n")

    with open(log_file, 'r') as f:
        for line_num, line in enumerate(f, 1):
            if error_pattern.search(line):
                errors.append((line_num, line.strip()))

    print(f"Found {len(errors)} errors:\n")

    for line_num, error in errors:
        print(f"Line {line_num}: {error}")

    return errors

if __name__ == "__main__":
    if len(sys.argv) != 2:
        print("Usage: python3 extract_errors.py <logfile>")
        sys.exit(1)

    extract_errors(sys.argv[1])
