#!/usr/bin/env python3
import requests
import time
from functools import wraps

def retry_on_failure(max_retries=3, delay=1, backoff=2):
    """Decorator to retry API calls on failure."""
    def decorator(func):
        @wraps(func)
        def wrapper(*args, **kwargs):
            retries = 0
            current_delay = delay

            while retries < max_retries:
                try:
                    response = func(*args, **kwargs)

                    # Success
                    if response.status_code == 200:
                        return response

                    # Rate limited (429) - wait and retry
                    if response.status_code == 429:
                        retry_after = int(response.headers.get('Retry-After', current_delay))
                        print(f"Rate limited. Waiting {retry_after}s...")
                        time.sleep(retry_after)
                    else:
                        print(f"Error {response.status_code}. Retrying in {current_delay}s...")
                        time.sleep(current_delay)

                    retries += 1
                    current_delay *= backoff

                except requests.RequestException as e:
                    print(f"Request failed: {e}. Retrying in {current_delay}s...")
                    time.sleep(current_delay)
                    retries += 1
                    current_delay *= backoff

            print(f"Failed after {max_retries} retries")
            return None

        return wrapper
    return decorator

@retry_on_failure(max_retries=3, delay=1, backoff=2)
def fetch_with_retry(url, headers=None):
    """Fetch data with automatic retries."""
    return requests.get(url, headers=headers or {})

if __name__ == "__main__":
    url = "https://api.github.com/rate_limit"
    response = fetch_with_retry(url)

    if response:
        data = response.json()
        print("Rate limit info:")
        print(f"  Remaining: {data['rate']['remaining']}/{data['rate']['limit']}")
