#!/usr/bin/env python3
"""GitHub API wrapper for common operations."""
import requests
import os

class GitHubAPI:
    def __init__(self, token=None):
        self.base_url = "https://api.github.com"
        self.token = token or os.environ.get('GITHUB_TOKEN')
        self.headers = {
            'Accept': 'application/vnd.github.v3+json'
        }
        if self.token:
            self.headers['Authorization'] = f"token {self.token}"

    def _request(self, method, endpoint, **kwargs):
        """Make an API request."""
        url = f"{self.base_url}{endpoint}"
        response = requests.request(method, url, headers=self.headers, **kwargs)
        response.raise_for_status()
        return response.json() if response.content else None

    def get_user(self, username):
        """Get user information."""
        return self._request('GET', f"/users/{username}")

    def list_repos(self, username):
        """List user's repositories."""
        return self._request('GET', f"/users/{username}/repos")

    def create_repo(self, name, description="", private=False):
        """Create a new repository."""
        data = {
            'name': name,
            'description': description,
            'private': private
        }
        return self._request('POST', "/user/repos", json=data)

    def create_issue(self, owner, repo, title, body="", labels=None):
        """Create an issue."""
        data = {
            'title': title,
            'body': body
        }
        if labels:
            data['labels'] = labels

        return self._request('POST', f"/repos/{owner}/{repo}/issues", json=data)

    def list_issues(self, owner, repo, state='open'):
        """List repository issues."""
        params = {'state': state}
        return self._request('GET', f"/repos/{owner}/{repo}/issues", params=params)

    def create_pr(self, owner, repo, title, head, base, body=""):
        """Create a pull request."""
        data = {
            'title': title,
            'head': head,
            'base': base,
            'body': body
        }
        return self._request('POST', f"/repos/{owner}/{repo}/pulls", json=data)

if __name__ == "__main__":
    # Example usage
    gh = GitHubAPI()

    # Get user info
    user = gh.get_user('octocat')
    print(f"User: {user['name']}")
    print(f"Followers: {user['followers']}")
    print()

    # List repos
    repos = gh.list_repos('octocat')
    print(f"Repositories: {len(repos)}")
    for repo in repos[:5]:
        print(f"  â€¢ {repo['name']} (â­ {repo['stargazers_count']})")
