#!/usr/bin/env python3
import psutil
import time
import smtplib
from email.mime.text import MIMEText
from datetime import datetime

class ResourceMonitor:
    def __init__(self, cpu_threshold=80, memory_threshold=80, disk_threshold=90):
        self.cpu_threshold = cpu_threshold
        self.memory_threshold = memory_threshold
        self.disk_threshold = disk_threshold
        self.alerts = []

    def check_cpu(self):
        """Check if CPU usage exceeds threshold."""
        cpu_percent = psutil.cpu_percent(interval=1)
        if cpu_percent > self.cpu_threshold:
            return f"CPU usage at {cpu_percent}% (threshold: {self.cpu_threshold}%)"
        return None

    def check_memory(self):
        """Check if memory usage exceeds threshold."""
        mem = psutil.virtual_memory()
        if mem.percent > self.memory_threshold:
            return f"Memory usage at {mem.percent}% (threshold: {self.memory_threshold}%)"
        return None

    def check_disk(self):
        """Check if disk usage exceeds threshold."""
        alerts = []
        for partition in psutil.disk_partitions():
            try:
                usage = psutil.disk_usage(partition.mountpoint)
                if usage.percent > self.disk_threshold:
                    alerts.append(f"Disk {partition.mountpoint} at {usage.percent}% (threshold: {self.disk_threshold}%)")
            except PermissionError:
                pass
        return alerts if alerts else None

    def monitor(self, check_interval=60):
        """Monitor resources and generate alerts."""
        print(f"Starting resource monitor (interval: {check_interval}s)")
        print(f"Thresholds: CPU={self.cpu_threshold}%, Memory={self.memory_threshold}%, Disk={self.disk_threshold}%\n")

        try:
            while True:
                timestamp = datetime.now().strftime("%Y-%m-%d %H:%M:%S")
                alerts_found = False

                # Check CPU
                cpu_alert = self.check_cpu()
                if cpu_alert:
                    print(f"[{timestamp}] âš ï¸  ALERT: {cpu_alert}")
                    alerts_found = True

                # Check Memory
                mem_alert = self.check_memory()
                if mem_alert:
                    print(f"[{timestamp}] âš ï¸  ALERT: {mem_alert}")
                    alerts_found = True

                # Check Disk
                disk_alerts = self.check_disk()
                if disk_alerts:
                    for alert in disk_alerts:
                        print(f"[{timestamp}] âš ï¸  ALERT: {alert}")
                    alerts_found = True

                if not alerts_found:
                    print(f"[{timestamp}] âœ“ All resources within normal range")

                time.sleep(check_interval)

        except KeyboardInterrupt:
            print("\nMonitoring stopped.")

if __name__ == "__main__":
    monitor = ResourceMonitor(cpu_threshold=80, memory_threshold=80, disk_threshold=90)
    monitor.monitor(check_interval=30)
