#!/usr/bin/env python3
import requests
from bs4 import BeautifulSoup
import time
from urllib.parse import urljoin, urlparse
import urllib.robotparser

class PoliteScraper:
    def __init__(self, delay=2, user_agent=None):
        self.delay = delay
        self.last_request_time = 0
        self.user_agent = user_agent or 'Mozilla/5.0 (compatible; PoliteScraper/1.0)'
        self.session = requests.Session()
        self.session.headers.update({'User-Agent': self.user_agent})

    def can_fetch(self, url):
        """Check robots.txt to see if we can scrape this URL."""
        try:
            parsed = urlparse(url)
            robots_url = f"{parsed.scheme}://{parsed.netloc}/robots.txt"

            rp = urllib.robotparser.RobotFileParser()
            rp.set_url(robots_url)
            rp.read()

            return rp.can_fetch(self.user_agent, url)
        except:
            # If we can't read robots.txt, assume we can fetch
            return True

    def fetch(self, url):
        """Fetch URL with rate limiting."""
        # Check robots.txt
        if not self.can_fetch(url):
            print(f"âš ï¸  robots.txt disallows scraping: {url}")
            return None

        # Rate limiting: wait if needed
        elapsed = time.time() - self.last_request_time
        if elapsed < self.delay:
            sleep_time = self.delay - elapsed
            print(f"â³ Waiting {sleep_time:.1f}s...")
            time.sleep(sleep_time)

        print(f"ðŸ“¥ Fetching: {url}")

        try:
            response = self.session.get(url, timeout=10)
            self.last_request_time = time.time()

            if response.status_code == 200:
                return BeautifulSoup(response.content, 'html.parser')
            else:
                print(f"  Error: {response.status_code}")
                return None
        except requests.RequestException as e:
            print(f"  Request failed: {e}")
            return None

    def scrape_urls(self, urls):
        """Scrape multiple URLs politely."""
        results = []

        for i, url in enumerate(urls, 1):
            print(f"\n[{i}/{len(urls)}]")
            soup = self.fetch(url)

            if soup:
                title = soup.find('title')
                results.append({
                    'url': url,
                    'title': title.text if title else 'No title',
                    'status': 'success'
                })
            else:
                results.append({
                    'url': url,
                    'title': None,
                    'status': 'failed'
                })

        return results

if __name__ == "__main__":
    scraper = PoliteScraper(delay=2)

    urls = [
        'https://example.com',
        'https://example.org',
        'https://example.net'
    ]

    results = scraper.scrape_urls(urls)

    print("\n" + "="*60)
    print("RESULTS")
    print("="*60)
    for result in results:
        status_icon = 'âœ“' if result['status'] == 'success' else 'âœ—'
        print(f"{status_icon} {result['url']}")
        if result['title']:
            print(f"  {result['title']}")
