#!/usr/bin/env python3
from watchdog.observers import Observer
from watchdog.events import FileSystemEventHandler
import time
from datetime import datetime

class FileChangeHandler(FileSystemEventHandler):
    def __init__(self, callback=None):
        self.callback = callback

    def on_created(self, event):
        if not event.is_directory:
            print(f"[{datetime.now().strftime('%H:%M:%S')}] Created: {event.src_path}")
            if self.callback:
                self.callback('created', event.src_path)

    def on_modified(self, event):
        if not event.is_directory:
            print(f"[{datetime.now().strftime('%H:%M:%S')}] Modified: {event.src_path}")
            if self.callback:
                self.callback('modified', event.src_path)

    def on_deleted(self, event):
        if not event.is_directory:
            print(f"[{datetime.now().strftime('%H:%M:%S')}] Deleted: {event.src_path}")
            if self.callback:
                self.callback('deleted', event.src_path)

def watch_directory(path, callback=None):
    """Watch directory for file changes."""
    print(f"Watching directory: {path}")
    print("Press Ctrl+C to stop\n")

    event_handler = FileChangeHandler(callback)
    observer = Observer()
    observer.schedule(event_handler, path, recursive=True)
    observer.start()

    try:
        while True:
            time.sleep(1)
    except KeyboardInterrupt:
        observer.stop()
        print("\nStopped watching.")

    observer.join()

if __name__ == "__main__":
    import sys

    if len(sys.argv) < 2:
        print("Usage: python3 watch_directory.py <directory>")
        sys.exit(1)

    watch_directory(sys.argv[1])
